package com.sugon.humanface_opencv.domain.entity;import lombok.Getter;import lombok.Setter;import javax.persistence.*;/** * Description: * Author     : Dawn.Ji * Date       : Created in 17:12 2018/11/7 */@Entity@Getter@Setter@Table(name = "t_human_face")public class THumanFace {    @Id    private String id;    private String idNum;    @Lob    @Basic    @Column(name = "face_image",columnDefinition = "mediumblob",nullable = false)    private byte[] faceImage;    private Integer label;}