package com.sugon.analysis;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.web.client.RestTemplateBuilder;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.client.RestTemplate;/** * @author: create by huangxb * @version: v1.0 * @description: * @date:2019/6/29 */@Configurationpublic class RestTemplateConfig {    @Autowired    private RestTemplateBuilder restTemplateBuilder;    @Bean    public RestTemplate restTemplate(){        return restTemplateBuilder.build();    }}