package com.sugon.analysis;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import springfox.documentation.builders.ApiInfoBuilder;import springfox.documentation.builders.PathSelectors;import springfox.documentation.builders.RequestHandlerSelectors;import springfox.documentation.service.ApiInfo;import springfox.documentation.service.Contact;import springfox.documentation.spi.DocumentationType;import springfox.documentation.spring.web.plugins.Docket;import springfox.documentation.swagger2.annotations.EnableSwagger2;/** * @author: create by huangxb * @version: v1.0 * @description: * @date:2019/10/24 */@Configuration@EnableSwagger2public class Swagger2 {    @Bean    public Docket controllerApi() {        return new Docket(DocumentationType.SWAGGER_2)                .apiInfo(new ApiInfoBuilder()                        .title("Swagger测试文档")                        .description("后台接口说明.")                        .contact(new Contact("ceshi", "", null))                        .version("版本号:1.0")                        .build())                .select()                .apis(RequestHandlerSelectors.basePackage("com.sugon.analysis.web"))                .paths(PathSelectors.any())                .build();    }}