package com.sugon.analysis.domain.data.object.oracle;import lombok.Data;import javax.persistence.*;/** * @program: zhzg-statistics->TPersonDo * @description: * @author: gaowh * @create: 2019-10-10 11:59 **/@Data@Entity@Table(name = "T_PERSON",schema = "NJGAZNL")public class TPersonDo {    /**     * ID     */    @Id    @Column(name = "ID")    private String id;    /**     * 身份证号码     */    @Column(name = "IDNO")    private String idNo;    /**     * 姓名     */    @Column(name = "PERSON_NAME")    private String personName;    /**     * 性别     */    @Column(name = "SEX")    private String sex;    /**     * 民族     */    @Column(name = "NATION")    private String nation;    /**     * 血型     */    @Column(name = "BLOOD_TYPE")    private String bloodType;    /**     * 出生日期     */    @Column(name = "BIRTHDAY")    private String birthDay;    /**     * 出生地     */    @Column(name = "BIRTH_PLACE")    private String birthPlace;    /**     * 籍贯     */    @Column(name = "NATIVE_PLACE")    private String nativePlace;    /**     * 政治面貌     */    @Column(name = "POLITICAL_STATUS")    private String politicalStatus;    /**     * 入党时间     */    @Column(name = "JOIN_PARTY_TIME")    private String joinPartyTime;    /**     * 毕业院校     */    @Column(name = "GRADUATED_FROM")    private String graduatedFrom;    /**     * 参加工作时间     */    @Column(name = "TIME_TO_WORK")    private String timeToWork;    /**     * 参加公安工作日期     */    @Column(name = "TIME_TO_WORK_PSB")    private String timeToWorkPsb;    /**     * 婚姻状况     */    @Column(name = "MARITAL_STATUS")    private String maritalStatus;    /**     * 健康状况     */    @Column(name = "HEALTH_STATUS")    private String healthStatus;    /**     * 个人专长     */    @Column(name = "PERSONAL_EXPERTISE")    private String personalExpertise;    /**     * 职务     */    @Column(name = "POST")    private String post;    /**     * 警衔     */    @Column(name = "POLICE_RANK")    private String policeRank;    /**     * 警号     */    @Column(name = "POLICE_NO")    private String policeNo;    /**     * 是否涉密     */    @Column(name = "SECRET_INVOLVED")    private String secertInvolved;    /**     * 所属单位id     */    @Column(name = "ORG_ID")    private String orgId;    /**     * 新增人     */    @Column(name = "ADDER")    private String adder;    /**     * 新增时间     */    @Column(name = "ADDTIME")    private String addTime;    /**     * 修改人     */    @Column(name = "MODER")    private String moder;    /**     * 修改时间     */    @Column(name = "MODTIME")    private String modTime;    /**     * 数据来源     */    @Column(name = "DATA_SOURCE")    private String dataSource;    /**     * 数据来源id     */    @Column(name = "DATA_SOURCE_ID")    private String dataSourceId;    /**     * 0正常 1删除     */    @Column(name = "DELSTATUS")    private String delStatus;    /**     * 户籍地址     */    @Column(name = "PERMANENT_ADDRESS")    private String permanentAddress;    /**     * 家庭住址     */    @Column(name = "HOME_ADDRESS")    private String homeAddress;    /**     * 科室简称     */    @Column(name = "OFFICE_SIMPLE_NAME")    private String officeSimpleName;    /**     * 单位简称     */    @Column(name = "ORG_SIMPLE_NAME")    private String orgSimpleName;    /**     *     */    @Column(name = "PERSON_SORT")    private String personSort;    /**     * 部门id     */    @Column(name = "ORG_DEPT_ID")    private String orgDeptId;    /**     *     */    @Column(name = "IDNO15")    private String idNo15;    /**     * 年龄     */    @Column(name = "PERSON_AGE")    private String age;    /**     * 进入来源     */    @Column(name = "SOURCE")    private String source;    /**     * 职级     */    @Column(name = "JOB_GRADE")    private String jobGrade;    /**     * 公安职务级别     */    @Column(name = "POLICE_JOB_GRADE")    private String policeJobGrade;    @Column(name = "PCLASSID")    private String pClassId;    /**专业名称*/    @Transient    private String professionalName;    /**拼接单位部门*/    @Transient    private String deptName;    public String getDeptName() {        return this.orgSimpleName+(this.officeSimpleName==null?"":this.officeSimpleName);    }    /**培训次数*/    @Transient    private int trainingCount;}