package com.sugon.analysis.domain.view;import com.sugon.analysis.enums.ResponsesStatus;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;/** * @author hxb */@Data @ApiModel(value = "返回数据实体", description = "返回数据") public class ResultVo {    @ApiModelProperty(value = "返回的数据", required = true, example = "null") private Object data;    @ApiModelProperty(value = "返回代码", required = true, example = "0") private int code;    @ApiModelProperty(value = "代码信息", required = true, example = "操作成功！") private String message;    public ResultVo() {        this.data = null;        this.code = ResponsesStatus.ERROR.getCode();        this.message = ResponsesStatus.ERROR.getMessage();    }    public void setResult(ResponsesStatus responsesStatus, Object data) {        this.data = data;        this.code = responsesStatus.getCode();        this.message = responsesStatus.getMessage();    }    public void setResult(ResponsesStatus responsesStatus) {        this.data = null;        this.code = responsesStatus.getCode();        this.message = responsesStatus.getMessage();    }    public void setResult(Object data) {        if (data == null) {            this.data = null;            this.code = ResponsesStatus.NODATA.getCode();            this.message = ResponsesStatus.NODATA.getMessage();        } else if (ResponsesStatus.ERROR.getMessage().equals(data)) {            this.code = ResponsesStatus.ERROR.getCode();            this.message = ResponsesStatus.ERROR.getMessage();        } else {            this.data = data;            this.code = ResponsesStatus.SUCCESS.getCode();            this.message = ResponsesStatus.SUCCESS.getMessage();        }    }}