package com.sugon.analysis.enums;/** * @author hxb */@SuppressWarnings({"ALL", "AlibabaEnumConstantsMustHaveComment"})public enum ResponsesStatus {    /**     * 操作成功     */    SUCCESS("操作成功!", 0),    /**     * 操作失败     */    FAILURE("操作失败!",500),    /**     * 暂无数据     */    NODATA("暂无数据!",1),    /**     * 模型名称不能为空     */    MODEL_NAME_NULL("模型名称不能为空！",1001),    /**     * 模型重复     */    MODEL_REPEAT("模型重复！",1002),    /**     * 没有人员ID     */    PROCESS_NOID("没有人员ID",2001),    /**     * 没有记录     */    NORECORD("没有记录！",998),    /**     * 内部错误     */    ERROR("内部错误",999),    PARAMSPECIAL("参数中含有特殊字符",8888),    NOIMAGE("未找到该人员图片信息",101),    /**     * 参数错误     */    PARAMERROR("参数错误",9999);    private String message;    private int code;    ResponsesStatus(String message, int code) {        this.message = message;        this.code = code;    }    public String getMessage() {        return this.message;    }    public int getCode() {        return this.code;    }}