package com.sugon.analysis.mappper.oracle;import com.sugon.analysis.domain.data.query.*;import com.sugon.analysis.entity.NationMapPojo;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import java.util.List;import java.util.Map;/** * @author: create by huangxb * @version: v1.0 * @description: * @date:2019/12/2 */@Mapperpublic interface PersonalAnalysisMapper {    /**     * 根据警号，姓名、身份证、部门id查询     * @param     * @return     */    List<PersonQo> pageQueryPeople(@Param("policeNo") String policeNo, @Param("personName") String personName, @Param("idNoList") List<String> idNoList, @Param("deptIds") List<String> deptIds, @Param("start") int start, @Param("end") int end );    /**     * 根据警号，姓名、身份证、部门id查询     * @param     * @return     */    Integer countPeople(@Param("policeNo") String policeNo,@Param("personName") String personName,@Param("idNoList") List<String> idNoList,@Param("deptIds") List<String> deptIds);    /**     * 根据身份证号统计进入来源     * @param idNos     * @return     */    List<SourceAndCount> querySourceCount(@Param("idNos") List<String> idNos);    /**     * 根据身份证号查询奖励信息     * @param idNo     * @return     */    List<YearCount> queryTcommenCount(@Param("idNo") String idNo);    /**     * 按年份统计培训总和     * @param idNo     * @return     */    List<YearCount> queryTrainCount(@Param("idNo") String idNo);    /**     * 查询职务更改的年份     * @param idNo     * @param dutys     * @return     */    List<DutyAndYear> queryDuty(@Param("idNo") String idNo,@Param("dutys") List<String> dutys);    /**     * 根据年份统计考核     * @param idNo     * @return     */    List<YearCount> queryAssessment(@Param("idNo") String idNo);    /**     * 查询所有的任职单位     * @param idNo     * @param startTime     * @return     */    List<String> queryAllOrgName(@Param("idNo") String idNo,@Param("startTime") String startTime);    /**     * 根据身份证号和时间查询     * @param idNo     * @param startTime     * @param endTime     * @return     */    List<AssessAndYear> queryAssessmentByYearAndIdNo(@Param("idNo") String idNo,                                                     @Param("startTime") String startTime,                                                     @Param("endTime") String endTime);    /**     * 根据时间和身份证号查询荣誉     * @param idNo     * @param startTime     * @param endTime     * @return     */    List<RewardAndCount> queryHornorByYearAndIdNo(@Param("idNo") String idNo,                                                  @Param("startTime") String startTime,                                                  @Param("endTime") String endTime);    /**     * 查询职级     * @param idNo     * @param no     * @return     */    List<DutyAndYear> queryDutyRank(@Param("idNo") String idNo,@Param("no") String no);    /**     * 查询满意度信息     * @param idNo     * @param startTime     * @param endTime     * @return     */    PersonAnalysisVo queryOpinion(@Param("idNo") String idNo, @Param("startTime") String startTime,                                  @Param("endTime") String endTime);    /**     * 根据年龄查询人员     * @param     * @return     */    List<PersonQo> queryPeopleByAge(@Param("idNoList")List<String> idNoList,@Param("startAge") Integer startAge,@Param("endAge") Integer endAge);    /**     * 根据入警年份查询     * @param     * @return     */    List<PersonQo> queryPeopleByJoinTime(@Param("startTime") String startTime,@Param("endTime") String endTime,@Param("idNoList") List<String> idNoList);    /**     * 根据工作时间查询     * @param     * @return     */    List<PersonQo> queryPeopleByWorkTime(@Param("idNoList") List<String> idNoList);    /**     * 查询职务     * @param idNo     * @param startTime     * @param endTime     * @return     */    List<DutyAndYear> queryDutyByTime(@Param("idNo") String idNo, @Param("startTime") String startTime,                                      @Param("endTime") String endTime);    /**     * 根据身份证号码集合查出对应的民族信息     * @param     * @return     */    List<NationMapPojo> queryNationByIdNum(@Param("idNoList") List<String> idNoList);    /**     * 根据身份证号码集合查出对应的任现职时间     * @param     * @return     */    List<Map<String,Object>> queryAppointmentTime(@Param("idNoList") List<String> idNoList);}