package com.sugon.analysis.platform.controller;import com.sugon.analysis.enums.result.ResultModel;import com.sugon.analysis.platform.dto.ModelUploadDTO;import com.sugon.analysis.platform.dto.ReportModelHistoryQuery;import com.sugon.analysis.platform.service.ReportModelManagerService;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;import javax.annotation.Resource;import javax.servlet.http.HttpServletResponse;import java.util.List;/** * @description: 模板管理控制层 * @author: liuwei * @create: 2020-05-27 15:56 **/@RestController@RequestMapping("/reportModelManager")@CrossOriginpublic class ReportModelManagerController {    @Resource    private ReportModelManagerService reportModelManagerService;    @RequestMapping("/uploadFile")    public ResultModel uploadFile(MultipartFile file){        return reportModelManagerService.uploadFile(file);    }    @RequestMapping("/downloadFile/{filePath:.+}")    public void downloadFile(@PathVariable("filePath") String filePath, HttpServletResponse response){        reportModelManagerService.downloadFile(filePath,response);    }    @RequestMapping("/uploadModel")    public ResultModel uploadModel(@RequestBody ModelUploadDTO modelUploadDTO){        return reportModelManagerService.uploadModel(modelUploadDTO);    }    @RequestMapping("/deleteModel")    public ResultModel deleteModel(@RequestBody List<String> ids){        return reportModelManagerService.deleteModel(ids);    }    @RequestMapping("/detail")    public ResultModel detail(String id){        return reportModelManagerService.detail(id);    }    @RequestMapping("/queryList")    public ResultModel queryList(@RequestBody ReportModelHistoryQuery query){        return reportModelManagerService.queryList(query);    }}