package com.sugon.analysis.repository.oracle;import com.sugon.analysis.domain.data.object.oracle.SysDeptDo;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;/** * @program: * @description: * @author: gaowh * @create: 2019-09-24 17:19 **/public interface SysDeptDao extends JpaRepository<SysDeptDo, String> {    /**     * 根据父节点找到所有的一级子节点并根据查询字段排序     * @param parentId     * @param delStatus     * @return     */    @Query(value = "SELECT * FROM NJGAZNL.SYS_DEPT WHERE PARENTID=?1 AND DELSTATUS=?2 AND ID NOT IN ('2014111018472495800000001181','2014111018472489500000000794','2001191147572760000000001778','2014111018472487900000000747') ORDER BY SEARCHCODE ",nativeQuery =true)    List<SysDeptDo> findAllByParentIdAndDelStatusOrderBySearchCode(String parentId, int delStatus);    /**     * 根据id查询     * @param id     * @param delStatus     * @return     */    SysDeptDo findByIdAndDelStatus(String id, int delStatus);    /**     * 根据idList查询     * @param idList     * @param delStatus     * @return     */    List<SysDeptDo> findAllByIdInAndDelStatus(List<String> idList, int delStatus);    /**     * 根据parentId查询     * @param parentId     * @param delStatus     * @return     */    @Query(value = "SELECT * FROM NJGAZNL.SYS_DEPT WHERE PARENTID=?1 AND DELSTATUS=?2 AND ID NOT IN ('2014111018472495800000001181','2014111018472489500000000794','2001191147572760000000001778','2014111018472487900000000747')  ",nativeQuery =true)    List<SysDeptDo> findByParentIdAndDelStatus(String parentId, int delStatus);}