package com.sugon.analysis.repository.sugon.oracle;import com.sugon.analysis.domain.data.object.sugon.oracle.KpiZGResult;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;public interface KpiZGResultDao extends JpaRepository<KpiZGResult, Long> {    /**     * 获取绩效考核组别菜单     *     * @return     */    @Query(value = "SELECT DISTINCT GROUPNAME FROM KPI_ZG_RESULT WHERE YEAR=:year AND QUARTER =:quarter ORDER BY TRANSLATE(GROUPNAME,'一二三四五六','123456')" ,nativeQuery = true)    public List<String> getKPIGroupMenu(@Param("year") String year, @Param("quarter") String quarter);    /**     * 获取绩效考核年份菜单     *     * @return     */    @Query(value = "SELECT DISTINCT YEAR FROM KPI_ZG_RESULT  ORDER BY YEAR" ,nativeQuery = true)    public List<String> getKPIYearMenu();    /**     * 获取绩效考核季度菜单     *     * @return     * @param year     */    @Query(value = "SELECT DISTINCT QUARTER FROM KPI_ZG_RESULT WHERE YEAR=:YEAR ORDER BY TRANSLATE(QUARTER,'一二三四五六','123456') DESC" ,nativeQuery = true)    public List<String> getKPIQuarterMenu(@Param("YEAR") String year);    /**     * 获取某一个单元的绩效     *     * @param groupName     * @param year     *@param quarter @return     */    @Query(value = "SELECT * FROM KPI_ZG_RESULT  WHERE GROUPNAME =:groupName AND YEAR =:year AND QUARTER=:quarter ORDER BY CAST(TOTAL_SCORE AS NUMBER) DESC",nativeQuery = true)    public List<KpiZGResult> findByGroupIs(@Param("groupName") String groupName, @Param("year") String year, @Param("quarter") String quarter);}