package com.sugon.analysis.util;import java.util.*;/** * @program: analysis->CollectionsSort * @description: * @author: gaowh * @create: 2019-10-29 14:23 **/public class CollectionsSort {    /**     * 对Map按value进行升序排序     */    public static <K, V extends Comparable<? super V>> Map<K, V> sortByValueDesc(Map<K, V> map) {        List<Map.Entry<K, V>> list = new LinkedList<>(map.entrySet());        list.sort(Comparator.comparing(Map.Entry::getValue));        Map<K, V> result = new LinkedHashMap<>();        for (Map.Entry<K, V> entry : list) {            result.put(entry.getKey(), entry.getValue());        }        return result;    }    /**     * 对Map按value进行降序排序     */    public static <K, V extends Comparable<? super V>> Map<K, V> sortByValueAsc(Map<K, V> map) {        List<Map.Entry<K, V>> list = new LinkedList<>(map.entrySet());        list.sort((o1, o2) -> -(o1.getValue()).compareTo(o2.getValue()));        Map<K, V> result = new LinkedHashMap<>();        for (Map.Entry<K, V> entry : list) {            result.put(entry.getKey(), entry.getValue());        }        return result;    }}