package com.sugon.analysis.web;import com.sugon.analysis.domain.data.query.TeamLeaderDetailQuery;import com.sugon.analysis.enums.result.ResultModel;import com.sugon.analysis.repository.oracle.SysDeptDao;import com.sugon.analysis.service.AsyncTaskService;import com.sugon.analysis.service.TeamLeaderBuildService;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.*;import javax.annotation.Resource;/** * @program: analysis->TeamBuildController * @description: * @author: gaowh * @create: 2019-10-28 15:24 **/@RestController@RequestMapping("/teamLeaderBuild")@Slf4jpublic class TeamLeaderBuildController {    @Resource    private TeamLeaderBuildService teamLeaderBuildService;    @Resource    private AsyncTaskService asyncTaskService;    @Resource    private SysDeptDao sysDeptDao;    @CrossOrigin    @RequestMapping(value = "/getLeaderDetail", method = RequestMethod.POST)    public ResultModel getDutyDetail(@RequestBody TeamLeaderDetailQuery query) {        return teamLeaderBuildService.getDetail(query);    }}